import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { FiLogIn } from 'react-icons/fi';
import api from '../../services/api';

import './styles.css';
import logoImg from '../../assets/logoGrande.png';
import heroesImg from '../../assets/heroes.png';

export default function Logon() {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const history = useHistory();

  async function handleLogin(e) {
    e.preventDefault();

    try {
      const response = await api.post('/login', { email, senha });
console.log({
  
})
      // Verifica se o login foi bem-sucedido com base na resposta do backend
      if (response.data) {
        // Armazene os dados do usuário (por exemplo, o token de autenticação) se necessário
        // Redirecione para a página após o login
        history.push('/dashboard'); // Substitua '/dashboard' pela rota apropriada
      } else {
        // Exiba uma mensagem de erro ao usuário, informando que o login falhou
        alert('Login falhou. Verifique suas credenciais.');
      }
    } catch (error) {
      // Lide com erros de solicitação, como falha de conexão com o backend
      console.error('Erro ao fazer login:', error);
      alert('Erro ao fazer login. Tente novamente mais tarde.');
    }
  }

  return (
    <div className="logon-container">
      <section className="form">
        <img src={logoImg} alt="Be The Hero" />
        <form onSubmit={handleLogin}>
          <h1> Faça seu Logon </h1>
          <input
            placeholder="Seu e-mail"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            placeholder="Sua senha"
            type="password" // Defina o tipo como "password" para ocultar a senha
            value={senha}
            onChange={(e) => setSenha(e.target.value)}
          />
          <button className="button" type="submit">
            Entrar
          </button>
          <Link className="back-link" to="/register">
            <FiLogIn size={16} color="#e02041" />
            Não tenho cadastro
          </Link>
        </form>
      </section>
      <img src={heroesImg} alt="Heroes" />
    </div>
  );
}
