import React, { useState, useEffect } from 'react'
import { Link, useHistory } from 'react-router-dom'
import { FiPower,FiMessageSquare, FiTrash2,FiSearch } from 'react-icons/fi'

import api from '../../services/api'

import './styles.css'

import logoImg from '../../assets/logo.svg'

export default function Profile() {

  const [restaurante, setRestaurante] = useState([])
  const history = useHistory()
  const restauranteId = localStorage.getItem('id')
  const nomeUsuario = localStorage.getItem('userName');

  useEffect(() => {
    api.get('restaurante', {
      headers: {
        Authorization: restauranteId,
      }
    }).then(response => {
      setRestaurante(response.data)
    })
  }, [restauranteId])



  useEffect(() => {
    api.get(`/restaurantes/search?name=${name}`, {
      headers: {
        Authorization: restauranteId,
      }
    }).then(response => {
      setRestaurante(response.data)
    })
  }, [restauranteId])
  
  // async function handleDeleteIncident(id) {
  //   try {
  //     await api.delete(`restaurante/${id}`, {
  //       headers: {
  //         Authorization: restauranteId,
  //       }
  //     })
      
  //     setRestaurante(restaurante.filter(incident => incident.id !== id))
  //   } catch (err) {
  //     alert('Erro ao deletar caso, tente novamente.')
  //   }
  // }

  function handleComent(id){
    history.push(`/comentario/${id}`);
  }    



  function handleLogout() {
    localStorage.clear()
    history.push('/')
  }

  return (
    <div className="profile-container">
      <header>
        <img src={logoImg} alt="Be The Hero" />
        <span> Bem-vindo/a, {nomeUsuario} </span>
      

        {/* <Link className="button" to="/incidents/new"> Cadastrar novo caso </Link> */}
        
          <input placeholder="Local"  />
          <input type="text" placeholder="Pesquisar Restaurante" />      
          <button onClick={handleLogout} type="button">
          <FiPower size={18} color="e02041" />
        </button>
      </header>
      <h1> Restaurantes </h1>
      <ul>
        {restaurante.map(restaurante => (
          <li key={restaurante.id}>
            <strong> Nome do Restaurante: </strong>
            <p> {restaurante.name} </p>
         
            <strong> Tipo de cozinha: </strong>
            <p> {restaurante.typeOfFood} </p>

            <strong> Local: </strong>
            <p> {restaurante.place} </p>
            
            <strong> Horario: </strong>
            <p> {restaurante.horario} </p>

            <strong> Contacto: </strong>
            <p> {restaurante.contact} </p>
    
           {/* <strong> VALOR Medio: </strong> */}
            {/* <p> {Intl.NumberFormat('pt-Mz', { style: 'currency', currency: 'MZN' }).format(incident.value)} </p> */}
    
            {/* <button onClick={() => handleDeleteIncident(restaurante.id)} type="button">
              <FiTrash2 size={20} color="#a8a8b3" />
            </button> */}
            <button onClick={() => handleComent(restaurante.id)} type="button">
              <FiMessageSquare size={20} color="#a8a8b3" />
            </button>
          </li>
        ))}
      </ul>
    </div>
  )
}
