import React, { useState } from 'react'
import { Link, useHistory } from 'react-router-dom'
import { FiArrowLeft } from 'react-icons/fi'
import axios from 'axios';

import api from '../../services/api'

import './styles.css'

import logoImg from '../../assets/logo.svg'

export default function NewIncident() {

  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [contact, setContact] = useState('')
  const [place, setPlace] = useState('')
  const [typeOfFood, setTypeOfFood] = useState('')
  const [horario, setHorario] = useState('')
  const [imagem, setImagem] = useState('')

  const history = useHistory()
  
  const ongId = localStorage.getItem('id')
  
  async function handleNewIncident(e) {
    e.preventDefault()
   const formData = new FormData();

// Adicione os campos do formulário
formData.append('name', 'Nome do Restaurante');
formData.append('email', 'email@exemplo.com');
formData.append('contact', '1234567890');
formData.append('place', 'Local do Restaurante');
formData.append('typeOfFood', 'Tipo de Comida');
formData.append('horario', 'Horário de Funcionamento');

// Adicione o campo de imagem
formData.append('imagem', imagem); // Onde 'arquivoDaImagem' é o arquivo da imagem que você deseja enviar

// Faça a solicitação POST com Axios
axios.post('http://localhost:3333/restaurantes', formData)
  .then(response => {
    console.log(response.data);
  })
  .catch(error => {
    console.error(error);
  });

  }

  return (
    <div className="new-incident-container">
      <div className="content">
        <section>
          <img src={logoImg} alt="Be The Hero" />
          <h1> Cadastrar Novo Caso </h1>
          <p> Descreva o caso detalhadamente para encontrar um herói para resolver isso. </p>
          <Link className="back-link" to="/profile">
            <FiArrowLeft size={16} color="#e02041" />
            Voltar para Home
          </Link>
        </section>
        <form onSubmit={handleNewIncident}>
          <input type="text" placeholder="Nome do Restaurante" value={name} onChange={(e) => setName(e.target.value)} />
          <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
          <input type="text" placeholder="Contacto" value={contact} onChange={(e) => setContact(e.target.value)} />
          <input type="text" placeholder="Local" value={place} onChange={(e) => setPlace(e.target.value)} />
          <input type="text" placeholder="Tipo de Comida" value={typeOfFood} onChange={(e) => setTypeOfFood(e.target.value)} />
          <input type="text" placeholder="Horário" value={horario} onChange={(e) => setHorario(e.target.value)} />
          <input type="file" onChange={(e) => setImagem(e.target.files[0])} /> {/* Input para a imagem */}
          <button type="submit">Adicionar Restaurante</button>
        </form>
      </div>
    </div>
  )
}
