import React, { useState } from 'react'
import { Link, useHistory } from 'react-router-dom'
import { FiLogIn } from 'react-icons/fi'

import api from '../../services/api'

import './styles.css'

import logoImg from '../../assets/logoGrande.png'
import heroesImg from '../../assets/heroes.png'

export default function Logon() {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');

  const history = useHistory();

  async function handleLogin(e) {
    e.preventDefault();
  
    const emailValue = email;
    const senhaValue = senha;

    try {
      const response = await api.post('/login', { email: emailValue, senha: senhaValue }); // Envie email e senha
      console.log({
        email: emailValue, senha: senhaValue
      });
      localStorage.setItem('email', emailValue);
      localStorage.setItem('password', senhaValue);

      localStorage.setItem('id', response.data.id);

      history.push('/profile');
    } catch (err) {
      alert('Falha no login, tente novamente.');
    }
  }


  return (
    <div className="logon-container">
      <section className="form">
        <img src={logoImg} alt="Be The Hero" />

      <form onSubmit={handleLogin}>
        <h1> Faça seu Logon </h1>
        <input placeholder="Seu e-mail" 
        value={email}
        onChange={(e) => setEmail(e.target.value)} />
       
        <input placeholder="Sua senha" 
        value={senha}
        onChange={(e) => setSenha(e.target.value)}/>

        <button className="button" type="submit"> Entrar </button>
        <Link className="back-link" to="/register">
          <FiLogIn size={16} color="#e02041" />
          Não tenho cadastro
        </Link>
      </form>
      </section>
      <img src={heroesImg} alt="Heroes" />
    </div>
  )
}
