import React, { useState, useEffect } from 'react'
import { useParams, useHistory } from 'react-router-dom'
import { FiPower,FiMessageSquare, FiTrash2,FiSearch } from 'react-icons/fi'

import api from '../../services/api'

import './styles.css'

import logoImg from '../../assets/logo.svg'

export default function Profile() {

  const [restaurante, setRestaurante] = useState([])
  const history = useHistory()
  const name = localStorage.getItem('name')
  const { restauranteId } = useParams();

  useEffect(() => {
    api.get('restaurante/', {
      headers: {
        Authorization: restauranteId,
      }
    }).then(response => {
      setRestaurante(response.data)
    })
  }, [restauranteId])




  function handleLogout() {
    localStorage.clear()
    history.push('/')
  }

  return (
    <div className="profile-container">

      {restaurante.map(restaurante => (
          <li key={restaurante.id}>
            <h1> {restaurante.name} </h1>
         
            <strong> Tipo de cozinha: </strong>
            <p> {restaurante.typeOfFood} </p>

            <strong> Local: </strong>
            <p> {restaurante.place} </p>
            
            <strong> Horario: </strong>
            <p> {restaurante.horario} </p>

            <strong> Contacto: </strong>
            <p> {restaurante.contact} </p>
            <button onClick={() => handleComent(restaurante.id)} type="button">
              <FiMessageSquare size={20} color="#a8a8b3" />
            </button>
          </li>
        ))}
    </div>
  )
}
